(601,"_site_transient_community-events-a138d024e4b921d2b5772312ad22302d","a:3:{s:9:\"sandboxed\";b:0;s:8:\"location\";a:1:{s:2:\"ip\";s:12:\"192.168.95.0\";}s:6:\"events\";a:5:{i:0;a:8:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:83:\"八王子WordPressミートアップ（同時開催Hello! WordPress in 八王子）\";s:3:\"url\";s:67:\"https://www.meetup.com/Tokyo-WordPress-Meetup/events/kjghmrybcdblb/\";s:6:\"meetup\";s:22:\"Tokyo WordPress Meetup\";s:10:\"meetup_url\";s:46:\"https://www.meetup.com/Tokyo-WordPress-Meetup/\";s:4:\"date\";s:19:\"2020-02-08 14:00:00\";s:8:\"end_date\";s:19:\"2020-02-08 16:00:00\";s:8:\"location\";a:4:{s:8:\"location\";s:15:\"Hachioji, Japan\";s:7:\"country\";s:2:\"jp\";s:8:\"latitude\";d:35.657875061035;s:9:\"longitude\";d:139.34031677246;}}i:1;a:8:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:111:\"道玄坂 WordPress Meetup #8 大きめな会社のコーポレートサイト制作ガイド @loftwork COOOP10\";s:3:\"url\";s:63:\"https://www.meetup.com/Tokyo-WordPress-Meetup/events/267927674/\";s:6:\"meetup\";s:22:\"Tokyo WordPress Meetup\";s:10:\"meetup_url\";s:46:\"https://www.meetup.com/Tokyo-WordPress-Meetup/\";s:4:\"date\";s:19:\"2020-02-14 19:30:00\";s:8:\"end_date\";s:19:\"2020-02-14 21:30:00\";s:8:\"location\";a:4:{s:8:\"location\";s:17:\"Tōkyō-to, Japan\";s:7:\"country\";s:2:\"jp\";s:8:\"latitude\";d:35.656063079833999;s:9:\"longitude\";d:139.69549560547;}}i:2;a:8:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:37:\"羽田 WordPress Meetup (2020年2月)\";s:3:\"url\";s:63:\"https://www.meetup.com/Tokyo-WordPress-Meetup/events/268027440/\";s:6:\"meetup\";s:22:\"Tokyo WordPress Meetup\";s:10:\"meetup_url\";s:46:\"https://www.meetup.com/Tokyo-WordPress-Meetup/\";s:4:\"date\";s:19:\"2020-02-23 10:00:00\";s:8:\"end_date\";s:19:\"2020-02-23 17:00:00\";s:8:\"location\";a:4:{s:8:\"location\";s:17:\"Tōkyō-to, Japan\";s:7:\"country\";s:2:\"jp\";s:8:\"latitude\";d:35.563999176025;s:9:\"longitude\";d:139.71577453613;}}i:3;a:8:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:51:\"WordPressもくもく会＠千葉　20年2月の会\";s:3:\"url\";s:63:\"https://www.meetup.com/Chiba-WordPress-Meetup/events/268050028/\";s:6:\"meetup\";s:22:\"Chiba WordPress Meetup\";s:10:\"meetup_url\";s:46:\"https://www.meetup.com/Chiba-WordPress-Meetup/\";s:4:\"date\";s:19:\"2020-02-23 14:00:00\";s:8:\"end_date\";s:19:\"2020-02-23 18:00:00\";s:8:\"location\";a:4:{s:8:\"location\";s:12:\"Chiba, Japan\";s:7:\"country\";s:2:\"jp\";s:8:\"latitude\";d:35.614555358887003;s:9:\"longitude\";d:140.1092376709;}}i:4;a:8:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:97:\"静岡 WordPress Meetup in 熱海 〜WordPressのセキュリティ対策 & お悩み相談会〜\";s:3:\"url\";s:66:\"https://www.meetup.com/Shizuoka-WordPress-Meetup/events/268163744/\";s:6:\"meetup\";s:25:\"Shizuoka WordPress Meetup\";s:10:\"meetup_url\";s:49:\"https://www.meetup.com/Shizuoka-WordPress-Meetup/\";s:4:\"date\";s:19:\"2020-02-24 15:20:00\";s:8:\"end_date\";s:19:\"2020-02-24 17:40:00\";s:8:\"location\";a:4:{s:8:\"location\";s:12:\"Atami, Japan\";s:7:\"country\";s:2:\"jp\";s:8:\"latitude\";d:35.096324920653998;s:9:\"longitude\";d:139.07412719727;}}}}","no"),
